sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue === oldValue ) {
		return;
	} 

	//client script
	var ga = new GlideAjax('GetEmailAddress'); //This argument should be the exact name of the script include.
	ga.addParam('sysparm_name', 'getEmail'); //sysparm_name is the name of the function in the script include to call.
	ga.addParam('sysparm_userID', g_form.getValue('u_requested_for')); //This is an optional parameter which we can reference in the script include.
	ga.getXML(populateEmailField); //This is our callback function, which will process the response.

	function populateEmailField(response) { //the argument 'response' is automatically provided when the callback funciton is called by the system.
		//Dig out the 'answer' attribute, which is what our function returns.
		var myAnswerFromXML = response.responseXML.documentElement.getAttribute('answer');
		//alert(myAnswerFromXML); //alert the result to make sure all is well.

			g_form.clearValue('u_requested_for_email');
			g_form.setValue('u_requested_for_email',myAnswerFromXML);

	}


}
