sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {

	var rightNow = new GlideDateTime();
	var assignee = current.assigned_to;

	var gr = new GlideRecord('x_58872_needit_needit_task');
	gr.addQuery('due_date','<=',rightNow);
	gr.addQuery('state','<',3);
	gr.addQuery('assigned_to',assignee);
	gr.query();

	if (gr.getRowCount() > 1) {

		template.print('In addition to this overdue NeedIt task, you have ' + (gr.getRowCount()-1) + " more overdue NeedIt Tasks.");
	}

})(current, template, email, email_action, event);
