 var rightNow = new GlideDateTime();

//  var futureTime = new GlideTime();
// 	 futureTime.setValue("23:00:00");
//  var twentyFour = new GlideDateTime();
// 	 twentyFour.add(futureTime);
    var twentyFour= new GlideDateTime();
		twentyFour.addSeconds(84600);

 gs.info('value of rightNow:' + rightNow);
 gs.info('value of twentyFour:' + twentyFour);

	var dueSoon = new GlideRecord('x_58872_needit_needit_task');
	    dueSoon.addQuery('due_date','>',rightNow);
		dueSoon.addQuery('due_date','<',twentyFour);
		dueSoon.addQuery('state','!=',3);
		dueSoon.query();

	while(dueSoon.next()){
		gs.info('These are the tasks that due soon: ' + dueSoon.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueSoon,dueSoon.number,gs.getUserName());
	}
